import React, { Component } from 'react';
import Web3 from 'web3';
import './App.css';
import Navbar from './Navbar';
import SocialNetwork from '../abis/SocialNetwork.json';
import Main from './Main';


class App extends Component {

    async componentDidMount(){
        await this.loadWeb3()
        await this.loadBlockChainData()
    }



  loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
       window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async getPostsOnCreation(){

      const postCount = await this.state.socialNetwork.methods.postCount().call()
      this.setState({postCount: postCount })

          const post = await this.state.socialNetwork.methods.posts(postCount).call()
          this.setState({
              posts : [...this.state.posts,post]
          })

  }




  async loadBlockChainData(){
      const web3 = window.web3
      const accounts = await web3.eth.getAccounts()
      console.log(accounts)
      this.setState({  account : accounts[0] })
      //fetch networkId
      const networkId = await web3.eth.net.getId()
      const networkData = SocialNetwork.networks[networkId]
      //console.log(networkId)
      //fetch address
      if(networkData)
      {
         const socialNetwork = await web3.eth.Contract(SocialNetwork.abi,networkData.address)
         //console.log(socialNetwork)
         this.setState({    socialNetwork : socialNetwork })  //left side is constructor's socialNetwork

         const postCount = await this.state.socialNetwork.methods.postCount().call()
         console.log(postCount)
         this.setState({postCount: postCount })

         for(var i = 1;i<=postCount;i++)
         {
             const post = await this.state.socialNetwork.methods.posts(i).call()
             this.setState({
                 posts : [...this.state.posts,post]
             })
         }
                // console.log({posts : this.state.posts})
          this.setState({ loading : false })
      }
      else {
          window.alert('SocialNetwork contract is not deployed to detected network')
      }
  }

  createPost =  async (content) => {
   await this.state.socialNetwork.methods.createPost(content).send({ from: this.state.account },
   async (err,txh)=> {
       if(err)
       {
           console.log(err)
       }
       else {
           console.log(txh)

       }
   })

 }

 creatingPost = async (content) => {
     await this.createPost(content)
     await this.getPostsOnCreation()
     console.log(`creatingPost`,his.state.postCount)
     this.setState({ loading : false })

 }


  constructor(props)
  {
      super(props)
      this.state = {
          account : '',
          socialNetwork : null,
          postCount : 0,
          posts : [],
          loading : true
      }


  }

  render() {
    return (
      <div>
        <Navbar account = {this.state.account}/>
     {this.state.loading
            ? <div id="loader" className="text-center mt-5"><p>Loading...</p></div>
            :
            <Main
            posts = {this.state.posts}
            creatingPost={this.creatingPost}
            />
         }
        </div>
    );
  }
}

export default App;
